import React, {Fragment, useEffect, useState} from "react";
import axios from "axios"
import ProductCard from "./ProductCard.js";

//PENDIENTES
// Poder ordenar los productos en base a su precio, de forma ascendete o descendente.
// Poder filtrar por condicion.
// Poder p치ginar los resultados de a 30 productos por p치gina.


const Catalogo = (props)=>{

    const [products, setProducts] = useState([]);

    const busqueda = props.palabra;

    const onSubmitForm = async (e) => {
         e.preventDefault();
        try {
                const response = await axios.get(`http://localhost:5001/api/search?q=${busqueda}`)
                const jsonData = response.data
                console.log(Array.isArray(jsonData));

                //const recursos = response.data.results;
                await setProducts(jsonData)

        } catch (err) {
            console.error("este es el error", err.message)
        }
    }


    console.log("soy el props del catalogo", props.palabra) 

    var sortF = () => {}
    var orderBy = 'priceASC'
    switch (orderBy) {
        case 'priceASC':
            sortF = (a, b) =>{
                if (a.price < b.price){
                    return -1
                }
                if(a.price > b.price){
                    return 1
                }
                return 0
            }
            break;
            case 'priceDSC':
                sortF = (a, b) =>{
                    if (a.price < b.price){
                        return 1
                    }
                    if(a.price > b.price){
                        return -1
                    }
                    return 0
                }
                break;
        default:
            break;
    }

    return(


        <Fragment>
            {" "}
            <div>COmoponente catalogo</div>
            <h1>{props.mascota}</h1>
            <form className=""  onSubmit={onSubmitForm}>
                <button type="submit">tocame bb</button>
            </form>
            <div>
            {products.sort(SVGComponentTransferFunctionElement).map((item) => {
                return (
                    <ProductCard
                    key = {item.id}

                    title={item.title}
                    price={item.price}
                    currency_id={item.currency_id}
                    available_quantity={item.available_quantity}
                    thumbnail= {item.thumbnail}
                    condition={item.condition}
                    />
                );
                })}

                </div>
        </Fragment>
    )

}


export default Catalogo
