import React, { Fragment, useEffect, useState } from "react";
import axios from "axios"
import ProductCard from "./ProductCard.js";

//>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>


const Catalogo = (props) => {

    var [products, setProducts] = useState([]);
    var [orderBy, setOrderBy] = useState("")
    const busqueda = props.palabra;

    const onSubmitForm = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.get(`http://localhost:5001/api/search?q=${busqueda}`)
            const jsonData = response.data
            console.log(Array.isArray(jsonData));

            //const recursos = response.data.results;
            await setProducts(jsonData)

        } catch (err) {
            console.error("este es el error", err.message)
        }
    }


    console.log("soy el props del catalogo", props.palabra)

    var sortF = () => {    
    }

    orderBy = 'priceASC'

   var prod = products.sort(sortF)

   const ordenMen = ()=>{
    setOrderBy ('priceASC')
}

const ordenMay = ()=>{

    setProducts(products= products.sort((a, b) => {
        if (a.price < b.price) {
            return 1
        } 
        if (a.price > b.price) {
            return -1
        }
        return 0
    }))
}

    switch (orderBy) {
        case 'priceASC':
            sortF = (a, b) => {
                if (a.price < b.price) {
                    return -1
                }
                if (a.price > b.price) {
                    return 1
                }
                return 0
            }
            break;
        case 'priceDSC':
            sortF = (a, b) => {
                if (a.price < b.price) {
                    return 1
                }
                if (a.price > b.price) {
                    return -1
                }
                return 0
            }
            break;
        default:
            break;
    }



    return (


        <Fragment>
            {" "}
            <div>COmoponente catalogo</div>
            <h1>{props.mascota}</h1>
            <form className="" onSubmit={onSubmitForm}>
                <button type="submit">tocame bb</button>
            </form>
                   <div className="btn-group btn-group-toggle" data-toggle="buttons">
                        <label className="btn btn-secondary active">
                            <input type="radio" name="options" id="option1" autocomplete="off" checked/> Active
                        </label>
                        <label className="btn btn-secondary" onClick={() =>ordenMen()} >
                            <input type="radio" name="options" id="option2" autocomplete="off"/> M- a M+
                        </label>
                        <label className="btn btn-secondary" onClick={() =>ordenMay()}  >
                            <input type="radio" name="options" id="option3" autocomplete="off"/> M+ a M-
                        </label>
                        <button onClick={() =>ordenMay()}> de mayor a menor bb</button>
                    </div>

            <div>
                {products.map((item) => {
                    return (
                        <ProductCard
                            key={item.id}

                            title={item.title}
                            price={item.price}
                            currency_id={item.currency_id}
                            available_quantity={item.available_quantity}
                            thumbnail={item.thumbnail}
                            condition={item.condition}
                        />
                    );
                })}

            </div>
        </Fragment>
    )

}


export default Catalogo
