import React, {Fragment, useEffect, useState} from "react";
import axios from "axios"
import ProductCard from "./ProductCard.js";

//PENDIENTES
// Poder ordenar los productos en base a su precio, de forma ascendete o descendente.
// Poder filtrar por condicion.
// Poder p치ginar los resultados de a 30 productos por p치gina.


const Catalogo = (props)=>{

    const [products, setProducts] = useState({
        productos: [],
        newProductos: [],
        usedProductos: [],
        lowestAll: [],
        lowestNew: [],
        lowestUsed: [],
        highestNew: [],
        highestAll: [],
        highestUsed: [],
      });
    const cache = {};

    const onSubmitForm = (props) => {

        const busqueda = props.palabra;
        axios.get(`http://localhost:8080/api/search?q=${busqueda}`)
          .then((res) => {
            if (res.data !== undefined) {
              const recursos = res.data;
              setProducts({
                ...products,
                productos: recursos,
              });
            } else {
              alert("Producto no encontrado");
            }
          })
          .catch((err) => console.log(err));
      };



    console.log("soy el props del catalogo", props.palabra) 

    return(
        <Fragment>
            <div>COmoponente catalogo</div>
            <h1>{props.mascota}</h1>
            <form className=""  onSubmit={onSubmitForm}>
                <button type="submit">tocame bb</button>
            </form>
            {products.map((item) => {
              return (
                <ProductCard
                  title={item.title}
                  price={item.price}
                  currency_id={item.currency_id}
                  available_quantity={item.available_quantity}
                  thumbnail= {item.thumbnail}
                  condition={item.condition}
                />
              );
            })}
        </Fragment>
    )

}

export default Catalogo

