import React, {Fragment, useEffect, useState} from "react";
import axios from "axios"
import ProductCard from "./ProductCard.js";

const Catalogo = (props)=>{

    const [products, setProducts] = useState("");

    const onSubmitForm = async e => {
        e.preventDefault();
        try {
            const busqueda = props.palabra;
            console.log("soy la busqueda", busqueda)
            const response = await axios.get(`https://api.mercadolibre.com/sites/MLA/search?q=${busqueda}`)
            console.log("soy el response",response.data.results)
            setProducts(response.data.results)  


        } catch (err) {
            console.error("este es el error", err.message)
        }
    }


    console.log("soy el props del catalogo", props.palabra) 

    return(
        <Fragment>
            <div>COmoponente catalogo</div>
            <h1>{props.mascota}</h1>
            <form className=""  onSubmit={onSubmitForm}>
                <button type="submit">tocame bb</button>
            </form>
            {products.map((item, i) => {
              return (
                <ProductCard
                  title={item.title}

                />
              );
            })}
        </Fragment>
    )

}

export default Catalogo
